import React from 'react';

function MenuItem({ title, description, imageName, price, count, add, remove }) {
  return (
    <div className="menu-item">
      <img src={require(`../images/${imageName}`)} alt={title} className="item-img" />
      <div className="item-info">
        <h3 className="item-title">{title}</h3>
        <p className="item-description">{description}</p>
        <div className="item-footer">
          <span className="item-price">${price.toFixed(2)}</span>
          <div className="quantity-controls">
            <button className="btn-remove" onClick={remove} disabled={count === 0}>-</button>
            <span>{count}</span>
            <button className="btn-add" onClick={add}>+</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default MenuItem;
